        this.router = router;
        this.dialog = dialog;
        this.authService = authService;
        this.cookieService = cookieService;
        this.dialogRef = dialogRef;
        this.wrongPassword = false;
        this.isLoading = false;
    }
    ngOnInit() {
        document.querySelector('body').setAttribute('themebg-pattern', 'theme1');
    }
